Smart::StatusCode ComponentHelloSubscriber::connectTimeServiceIn(const std::string &serverName, const std::string &serviceName) {
	Smart::StatusCode status;
	
	std::cout << "connecting to: " << serverName << "; " << serviceName << std::endl;
	status = timeServiceIn->connect(serverName, serviceName);
	while(status != Smart::SMART_OK)
	{
		ACE_OS::sleep(ACE_Time_Value(0,500000));
		status = COMP->timeServiceIn->connect(serverName, serviceName);
	}
	std::cout << "connected.\n";
	timeServiceIn->subscribe(connections.timeServiceIn.interval);
	return status;
}


