Smart::StatusCode ComponentHelloSubscriber::connectAndStartAllServices() {
	Smart::StatusCode status = Smart::SMART_OK;
	
	status = connectTimeServiceIn(connections.timeServiceIn.serverName, connections.timeServiceIn.serviceName);
	if(status != Smart::SMART_OK) return status;
	return status;
}

