	const char* CommTimeCore::getCompiledHash()
	{
		return DomainHelloServicesIDL::REPO_HASH;
	}
	
	void CommTimeCore::getAllHashValues(std::list<std::string> &hashes)
	{
		// get own hash value
		hashes.push_back(getCompiledHash());
	}
	
	void CommTimeCore::checkAllHashValues(std::list<std::string> &hashes)
	{
		// check own hash value
		if (strcmp(getCompiledHash(), hashes.front().c_str()) != 0)
		{
			std::cerr << "###################################################" << std::endl;
			std::cerr << "WARNING: HASHES OF COMMUNICATION OBJECTS MISSMATCH!" << std::endl;
			std::cerr << "CommTimeCore hash" << std::endl;
			std::cerr << "Expected: " << getCompiledHash() << std::endl;
			std::cerr << "Received: " << hashes.front() << std::endl;
			std::cerr << "###################################################" << std::endl;
		}
		assert(strcmp(getCompiledHash(), hashes.front().c_str()) == 0);
		hashes.pop_front();
		
	}
	
	#ifdef ENABLE_HASH
	size_t CommTimeCore::generateDataHash(const DATATYPE &data)
	{
		size_t seed = 0;
		
		boost::hash_combine(seed, data.sec);
		boost::hash_combine(seed, data.usec);
		
		return seed;
	}
	#endif
	
	// default constructor
	CommTimeCore::CommTimeCore()
	:	idl_CommTime()
	{  
		setSec(0);
		setUsec(0);
	}
	
	CommTimeCore::CommTimeCore(const DATATYPE &data)
	:	idl_CommTime(data)
	{  }

	CommTimeCore::~CommTimeCore()
	{  }
	
	void CommTimeCore::to_ostream(std::ostream &os) const
	{
	  os << "CommTime(";
	  os << getSec() << " ";
	  os << getUsec() << " ";
	  os << ") ";
	}
	
	// convert to xml stream
	void CommTimeCore::to_xml(std::ostream &os, const std::string &indent) const {
		os << indent << "<sec>" << getSec() << "</sec>";
		os << indent << "<usec>" << getUsec() << "</usec>";
	}
	
	// restore from xml stream
	void CommTimeCore::from_xml(std::istream &is) {
		static const Smart::KnuthMorrisPratt kmp_sec("<sec>");
		static const Smart::KnuthMorrisPratt kmp_usec("<usec>");
		
		if(kmp_sec.search(is)) {
			unsigned long int secItem;
			is >> secItem;
			setSec(secItem);
		}
		if(kmp_usec.search(is)) {
			unsigned long int usecItem;
			is >> usecItem;
			setUsec(usecItem);
		}
	}
	
