	// restore from xml stream
	void Nav_msgs_MapMetaDataCore::from_xml(std::istream &is) {
		static const Smart::KnuthMorrisPratt kmp_map_load_time("<map_load_time>");
		static const Smart::KnuthMorrisPratt kmp_resolution("<resolution>");
		static const Smart::KnuthMorrisPratt kmp_width("<width>");
		static const Smart::KnuthMorrisPratt kmp_height("<height>");
		static const Smart::KnuthMorrisPratt kmp_origin("<origin>");
		
		if(kmp_map_load_time.search(is)) {
			CommBasicObjects::CommTimeStamp map_load_timeItem;
			map_load_timeItem.from_xml(is);
			setMap_load_time(map_load_timeItem);
		}
		if(kmp_resolution.search(is)) {
			float resolutionItem;
			is >> resolutionItem;
			setResolution(resolutionItem);
		}
		if(kmp_width.search(is)) {
			unsigned int widthItem;
			is >> widthItem;
			setWidth(widthItem);
		}
		if(kmp_height.search(is)) {
			unsigned int heightItem;
			is >> heightItem;
			setHeight(heightItem);
		}
		if(kmp_origin.search(is)) {
			ROSCommon_msgs::Geometry_msgs_Pose originItem;
			originItem.from_xml(is);
			setOrigin(originItem);
		}
	}
	
