	void Std_msgs_HeaderCore::to_ostream(std::ostream &os) const
	{
	  os << "Std_msgs_Header(";
	  os << getSeq() << " ";
	  getStamp().to_ostream(os);
	  os << getFrame_id() << " ";
	  os << ") ";
	}
	
	// convert to xml stream
	void Std_msgs_HeaderCore::to_xml(std::ostream &os, const std::string &indent) const {
		os << indent << "<seq>" << getSeq() << "</seq>";
		os << indent << "<stamp>";
		getStamp().to_xml(os, indent);
		os << indent << "</stamp>";
		os << indent << "<frame_id>" << getFrame_id() << "</frame_id>";
	}
	
	// restore from xml stream
	void Std_msgs_HeaderCore::from_xml(std::istream &is) {
		static const Smart::KnuthMorrisPratt kmp_seq("<seq>");
		static const Smart::KnuthMorrisPratt kmp_stamp("<stamp>");
		static const Smart::KnuthMorrisPratt kmp_frame_id("<frame_id>");
		
		if(kmp_seq.search(is)) {
			unsigned int seqItem;
			is >> seqItem;
			setSeq(seqItem);
		}
		if(kmp_stamp.search(is)) {
			CommBasicObjects::CommTimeStamp stampItem;
			stampItem.from_xml(is);
			setStamp(stampItem);
		}
		if(kmp_frame_id.search(is)) {
			std::string frame_idItem;
			is >> frame_idItem;
			setFrame_id(frame_idItem);
		}
	}
	
