	void Std_msgs_MultiArrayDimensionCore::to_ostream(std::ostream &os) const
	{
	  os << "Std_msgs_MultiArrayDimension(";
	  os << getLabel() << " ";
	  os << getSize() << " ";
	  os << getStride() << " ";
	  os << ") ";
	}
	
	// convert to xml stream
	void Std_msgs_MultiArrayDimensionCore::to_xml(std::ostream &os, const std::string &indent) const {
		os << indent << "<label>" << getLabel() << "</label>";
		os << indent << "<size>" << getSize() << "</size>";
		os << indent << "<stride>" << getStride() << "</stride>";
	}
	
	// restore from xml stream
	void Std_msgs_MultiArrayDimensionCore::from_xml(std::istream &is) {
		static const Smart::KnuthMorrisPratt kmp_label("<label>");
		static const Smart::KnuthMorrisPratt kmp_size("<size>");
		static const Smart::KnuthMorrisPratt kmp_stride("<stride>");
		
		if(kmp_label.search(is)) {
			std::string labelItem;
			is >> labelItem;
			setLabel(labelItem);
		}
		if(kmp_size.search(is)) {
			unsigned int sizeItem;
			is >> sizeItem;
			setSize(sizeItem);
		}
		if(kmp_stride.search(is)) {
			unsigned int strideItem;
			is >> strideItem;
			setStride(strideItem);
		}
	}
	
